import React, {useState} from 'react';
import SockJS from 'sockjs-client';
import {over} from 'stompjs';

interface message {
  receiver:string;
  sender:string;
  message:string;
}



var stompClient: any = null;
const SearchingPage = () => {
    const [userName, setUserName] = useState<string>("");
    const [isConnected, setIsConnected] = useState<boolean>(false);
  const connectToServer= () => {
    let Sock=new SockJS('http://localhost:8082/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected);

  }
  const onConnected = () => {
    setIsConnected(true);
    stompClient.subscribe('/user/' + userName + '/private', onMessageReceived);

  }

  const onMessageReceived = (payload:any) => {
    console.log('Your name is: ' + payload.body);

  }


  const startSearching = () => {
    stompClient.send('/app/findEnemy', {}, userName);
  }
  const connect = () => {
    connectToServer();
  }

  const updateUserName = (event: any) => {
    const value = event.target.value;
    setUserName(value);
    console.log(value);
  }

  return (
    <div className="App">
      {!isConnected? <div>connect to search</div>: <button onClick={startSearching}>search for opponent</button>}
      <div>
        {isConnected? <p>{userName}</p> :
        <div>
         <input id='user-name' placeholder='enter user name' value={userName} name='username' onChange={updateUserName}/>
         <button onClick={connect}>connect to server</button>
        </div>
        }
      </div>


    </div>
  );
}


export default SearchingPage
