import React, {useState} from 'react'


interface UserNameProps {
    userName: string;
    stompClient: any;
    gameID: string;
}

const GamePage: React.FC<UserNameProps> = ({userName, stompClient, gameID}) => {

    const [points, setPoints] = useState<number>(0);
    const [enemyPoints, setEnemyPoints] = useState<number>(0);

  const increase = () => {

      let message:string = userName + ":" + gameID;
      fetch(serverURL + '/button', {
        method: 'POST',
        headers: {'Content-Type': 'text/plain',},
        body: message,
      });
      fetch(serverURL + `/button/getPoints/${userName}/${gameID}`)
      .then((res) => res.json())
      .then((data:number ) => {
        setPoints(data);
      }).catch(console.error);
      stompClient.send('/app/sendToEnemy', {}, userName);

  }


  return (
    <div>
        <div>{userName}</div>
        <button onClick={increase}>Click</button>
        <p>You points: {points}</p>
        <p>Enemy points: {enemyPoints}</p>

    </div>
  )
}

export default GamePage
